  const nickname = req.body.nickname;
  const pwd = req.body.password;

  // 입력된 비밀번호 암호화
  bcrypt.genSalt(10, function (err, salt) {
    if (err) throw err;
    bcrypt.hash(pwd, salt, function (err, hash) {
      if (err) throw err;

      /*DB에 저장될 유저 정보 형식*/
