          console.log('중복된 닉네임입니다.');
        } else {

          db.query('INSERT INTO users SET ?', user, function (err, newUser) {
            if (err) throw err;
            console.log('welcome', newUser);
          })
        }
      })

    });
  });
  res.redirect('/login');
})

module.exports = router;

